import React, {Component} from 'react';
import axios from 'axios';

class App extends Component {


  state = {
    stateObj:{
      username:"",
      password:"",
      loggedIn:false,
      text: "Welcome"
    },
  }

  componentDidMount(){
    axios.get('http://localhost:8080/MySocialMedia/Login')
      .then((response) => {
        this.handleServerResponse(response, "Welcome");
      })
  }

  handleServerResponse = (response, textVal) =>{
    var loggedInVal=false;

    if(response.data.loggedIn === true){
      loggedInVal=true;
      textVal = "Welcome, Tejender!"
    }
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        loggedIn:loggedInVal,
        text:textVal
      }
    })

    console.log(response.data);

  }

  loginHandler = () => {
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        text:"loading"
      }
    });
    var parameters= {
      username:this.state.stateObj.username,
      password:this.state.stateObj.password
    }    
      axios.post('http://localhost:8080/MySocialMedia/Login',null,{ params:parameters})
      .then((response) => {
        this.handleServerResponse(response,"Username and Password Don't match");
      })

  }

  loginHandler = () => {
      axios.get('http://localhost:8080/MySocialMedia/Logout')
      .then((response) => {
        this.handleServerResponse(response,"Welcome, Please Sign in");
      })

  }

  usernameChangeHandler = (event) => {
  this.setState({
    stateObj:{
      ...this.state.stateObj,
      username:event.target.value
    }
  })    
  }

  passwordChangeHandler = (event) => {
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        password:event.target.value
      }
    });
  }

  render(){
    return (

          <div>
            {this.state.stateObj.loggedIn?
            <div>
              <input name="username" value={this.state.stateObj.username} onChange={this.usernameChangeHandler}/>
              <input name="password" value={this.state.stateObj.password} onChange={this.passwordChangeHandler}/>
              <button onClick={this.loginHandler}>Login</button>
              <p>{this.state.stateObj.text}</p>
            </div>
            :
            <button onClick={this.logoutHandler}>Logout</button>
            }
          </div>
    );  
  }
}

export default App;
