import React, {Component} from 'react';
import axios from 'axios';

class App extends Component {


  state = {
    stateObj:{
      username:"",
      password:"",
      loggedIn:false,
      text: "Welcome"
    },
  }

  componentDidMount(){
    axios.get('http://localhost:8080/MySocialMedia/Login')
      .then((response) => {
        this.handleServerResponse(response);
      })
  }

  handleServerResponse = (response) =>{
    if(response.data.loggedIn === true){
      this.setState({
        stateObj:{
          ...this.state.stateObj,
          loggedIn:true
        }
      })
    }
    console.log(response.data);

  }

  loginHandler = () => {
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        text:"loading"
      }
    })    
      axios.post('http://localhost:8080/MySocialMedia/Login', {username:this.state.stateObj.username,password:this.state.stateObj.password})
      .then((response) => {
        this.handleServerResponse(response);
      })

  }

  usernameChangeHandler = (event) => {
  this.setState({
    stateObj:{
      ...this.state.stateObj,
      username:event.target.value
    }
  })    
  }

  passwordChangeHandler = (event) => {
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        password:event.target.value
      }
    });
  }

  render(){
    return (
      <div>
        <input name="username" value={this.state.stateObj.username} onChange={this.usernameChangeHandler}/>
        <input name="password" value={this.state.stateObj.password} onChange={this.passwordChangeHandler}/>
        <button onClick={this.loginHandler}>Login</button>
        <p>{this.state.stateObj.text}</p>
        {console.log(this.state)}
      </div>
    );  
  }
}

export default App;
