import React, {Component} from 'react';
import axios from 'axios';
import LoginForm from './containers/Login/LoginFormForm'

const welcomeText = "Welcome, Please Sign in"
class App extends Component {


  state = {
    stateObj:{
      username:"",
      password:"",
      loggedIn:false,
      text: welcomeText
    },
  }

  componentDidMount(){
    axios.get('http://localhost:8080/MySocialMedia/Login')
      .then((response) => {
        this.handleServerResponse(response, welcomeText);
      })
  }

  handleServerResponse = (response, textVal) =>{
    var loggedInVal=false;

    if(response.data.loggedIn === true){
      loggedInVal=true;
      textVal = "Welcome, Tejender!"
    }
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        loggedIn:loggedInVal,
        text:textVal
      }
    })

    console.log(response.data);

  }

  loginHandler = () => {
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        text:"loading"
      }
    });
    var parameters= {
      username:this.state.stateObj.username,
      password:this.state.stateObj.password
    }    
      axios.post('http://localhost:8080/MySocialMedia/Login',null,{ params:parameters})
      .then((response) => {
        this.handleServerResponse(response,"Username and Password Don't match");
      })

  }

  logoutHandler = () => {
      axios.get('http://localhost:8080/MySocialMedia/Logout')
      .then((response) => {
        this.handleServerResponse(response,welcomeText);
      })

  }

  usernameChangeHandler = (event) => {
  this.setState({
    stateObj:{
      ...this.state.stateObj,
      username:event.target.value
    }
  })    
  }

  passwordChangeHandler = (event) => {
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        password:event.target.value
      }
    });
  }

  render(){
    return (

          <div>
            {this.state.stateObj.loggedIn?
            <div>
              <ul>
                <li> home </li>
                <li> messages </li>
                <li> explore </li>
                <li> notifications </li>
                <li onClick={this.logoutHandler}> logout </li>
              </ul>
            </div>
            :
            <div>
              <LoginForm
                username={this.state.stateObj.username}
                password={this.state.stateObj.password}
                logoutHandler={this.logoutHandler}
                loginHandler={this.loginHandler}


              />
            </div>
            }
          </div>
    );  
  }
}

export default App;
