import React, {Component} from 'react';
import axios from 'axios';

class App extends Component {


  state = {
    stateObj:{
      username:null,
      password:null,
      loggedIn:false,
      text: "Welcome"
    },
  }

  componentDidUpdate(){
    axios.get('http://localhost:8080/MySocialMedia/Login')
      .then((response) => {
        console.log('abc')
      })
  }

  loginHandler = () => {
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        text:"loading"
      }
    })    
      axios.get('http://localhost:8080/MySocialMedia/Login')
      .then((response) => {
        console.log('abc')
      })

  }

  usernameChangeHandler = (event) => {
  this.setState({
    stateObj:{
      ...this.state.stateObj,
      username:event.target.value
    }
  })    
  console.log(this.state);          
  }

  passwordChangeHandler = () => {
    this.setState({
      stateObj:{
        ...this.state.stateObj,
        password:event.target.value
      }
    });
    console.log(this.state);          
  }

  render(){
    return (
      <div>
        <input name="username" value={this.state.username} onChange={(event) => this.usernameChangeHandler.bind(this)}/>
        <input name="password" value={this.state.password} onChange={(event) => this.passwordChangeHandler.bind(this)}/>
        <button onClick={this.loginHandler}/>
        <p>{this.state.text}</p>
      </div>
    );  
  }
}

export default App;
